mapper.update:
UPDATE cart_items

    SET quantity = #{quantity}

WHERE user_id = #{userId} AND item_id = #{itemId}

mapper.delete:
DELETE FROM cart_items


        AND user_id = #{userId}


        AND item_id = #{itemId}


        AND item_type = #{itemType}


        AND quantity = #{quantity}


        AND dish_flavor = #{dishFlavor}
